	for(int i = 0;i < c_num - 1;i++)
		cout << wordbook_ans[codes[i]] << " ";
	cout << wordbook_ans[codes[c_num - 1]];
	return; 
}

int main(){
	cin >> n;
	cin >> num_str;
	for(int i = 1;i <= n;i++){
		//单词和代码的转义 
		cin >> wordbook_ans[i];
		wordbook_code[i] = num_turning(wordbook_ans[i]);
	}
	translating(0);
	if(!isFindout) cout << "No Solutions!";
	return 0;
